UNT_BANK, LOAN_PAYMENT.FLOAN_PAYMENT_ACCOUNT_NUM, LOAN_PAYMENT.FIS_RECEIVE_PAYMENT FROM SURETY.T_SURETY_GUARANTEE GUARANTEE LEFT JOIN SURETY.T_SURETY_G_PARTNER PARTNER ON GUARANTEE.FID = PARTNER.FGUARANTEE_ID LEFT JOIN SURETY.T_ERP_PERSON PERSON ON PERSON.FID = PARTNER.FMANAGER_ID LEFT JOIN SURETY.T_ERP_ORG ORG ON ORG.FID = PERSON.FORG_ID LEFT JOIN SURETY.T_SURETY_G_ESTATE_SELLER ESTATE_SELLER ON GUARANTEE.FID = ESTATE_SELLER.FGUARANTEE_ID LEFT JOIN SURETY.T_SURETY_G_PERSONINFO_TRADES PERSONINFO_TRADES ON ESTATE_SELLER.FID = PERSONINFO_TRADES.FESTATE_ID LEFT JOIN SURETY.T_SURETY_G_PERSONINFO_TRADES BUYER_TRADES ON GUARANTEE.FID = BUYER_TRADES.FGUARANTEE_ID AND BUYER_TRADES.FESTATE_ID IS NULL LEFT JOIN SURETY.T_SURETY_G_LOAN_SELLER LOAN_SELLER ON GUARANTEE.FID = LOAN_SELLER.FGUARANTEE_ID LEFT JOIN SURETY.T_SURETY_G_INFO_OF_TRANSACTION TRANSACTION ON GUARANTEE.FID = TRANSACTION.FGUARANTEE_ID LEFT JOIN SURETY.T_SURETY_G_LOAN_BUYER LOAN_BUYER ON GUARANTEE.FID = LOAN_BUYER.FGUARANTEE_ID LEFT JOIN SURETY.T_SURETY_G_GUARANTEE_SITUATION GUARANTEE_SITUATION ON GUARANTEE.FID = GUARANTEE_SITUATION.FGUARANTEE_ID LEFT JOIN SURETY.T_SURETY_G_ANTI_GUARANTOR ANTI_GUARANTOR ON GUARANTEE.FID = ANTI_GUARANTOR.FGUARANTEE_ID LEFT JOIN SURETY.T_SURETY_G_ANTI_ESTATE ANTI_ESTATE ON ANTI_GUARANTOR.FID = ANTI_ESTATE.FANTI_GUARANTOR_ID LEFT JOIN SURETY.T_SURETY_G_LOAN_GENERAL LOAN_GENERAL ON GUARANTEE.FID = LOAN_GENERAL.FGUARANTEE_ID LEFT JOIN SURETY.T_SURETY_G_LOAN_DETAILS LOAN_DETAILS ON GUARANTEE.FID = LOAN_DETAILS.FGUARANTEE_ID LEFT JOIN SURETY.T_SURETY_G_TRANS_RECEPAY_PLAN TRANS_RECEPAY_PLAN ON GUARANTEE.FID = TRANS_RECEPAY_PLAN.FGUARANTEE_ID LEFT JOIN SURETY.T_SURETY_G_LOAN_PAYMENT LOAN_PAYMENT ON GUARANTEE.FID = LOAN_PAYMENT.FGUARANTEE_ID WHERE GUARANTEE.FID = #{id}

